  `is_delete` tinyint(4) NOT NULL DEFAULT '0' COMMENT '单据是否作废标识',
  `payment_in` int(11) DEFAULT NULL COMMENT '支付渠道：0未选择 1 ATM、2 柜台、3 网银、4 支付宝、5 其它',
  `payer_name` varchar(50) DEFAULT NULL COMMENT '如果是我们收款，则为打款人姓名，如果是我们付款，则为收款人姓名',
  `payamount` double DEFAULT NULL COMMENT '打款金额，目前是由客户提供，客服手工录入',
  `paydate` datetime DEFAULT NULL COMMENT '打款时间，目前是由客户提供，客服手工录入',
  `payment_remarks` varchar(255) DEFAULT NULL COMMENT '打款备注',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=758 DEFAULT CHARSET=utf8 COMMENT='售后单费用单主要信息';

