  `portage` float(15,2) NOT NULL COMMENT '预计运费',
  `pay_status` tinyint(3) unsigned NOT NULL COMMENT '0:未打款 1：与预计总金额不相等 2：与预计金额相等 3：打款已完成',
  `pay_user` varchar(20) DEFAULT '' COMMENT '付款人',
  `tax_point` float(15,3) DEFAULT '0.000' COMMENT '税点',
