  `transform_count` int(11) NOT NULL DEFAULT '0' COMMENT '转换次数',
  `remark2` varchar(255) DEFAULT '' COMMENT '强制打款完成备注',
  `balance_mode` int(2) NOT NULL DEFAULT '0' COMMENT '结算模式[1:闪购; 2:经销; 4:代销]',
  `ticket` int(2) NOT NULL DEFAULT '0' COMMENT '是否有票 0：有票 1:无票',
  `complete_status` int(2) DEFAULT '0' COMMENT '是否到货完成',
  `type` int(2) NOT NULL DEFAULT '0' COMMENT '订单类型[0:我司仓; 1:非我司仓; 2:B2B采购]',
