-- ----------------------------
-- Table structure for finance_sell
-- ----------------------------
DROP TABLE IF EXISTS `finance_sell`;
CREATE TABLE `finance_sell` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',
  `order_id` int(10) NOT NULL DEFAULT '0' COMMENT '订单id',
  `code` varchar(45) NOT NULL DEFAULT '' COMMENT '单据号',
  `adjust_code` varchar(16) DEFAULT NULL COMMENT '调整单号',
  `price` float(15,2) NOT NULL DEFAULT '0.00' COMMENT '实际发生金额(不含运费)',
  `carriage` float(15,2) NOT NULL DEFAULT '0.00' COMMENT '运费',
  `charge` float(15,2) NOT NULL DEFAULT '0.00' COMMENT '费用',
  `buy_mode` tinyint(3) NOT NULL DEFAULT '0' COMMENT '购买方式,0:货到付款，1：在线支付，2：银行汇款，3：售后换货，4：混合支付',
  `pay_mode` tinyint(3) NOT NULL DEFAULT '0' COMMENT '付款方式',
  `deliver_type` int(10) NOT NULL DEFAULT '0' COMMENT '结算快递公司',
  `create_datetime` datetime NOT NULL COMMENT '操作发生时间',
  `packagenum` varchar(20) NOT NULL COMMENT '包裹单号',
  `data_type` int(10) NOT NULL DEFAULT '0' COMMENT '数据类别',
  `addr_sub1` int(10) NOT NULL DEFAULT '0' COMMENT '一级地址id',
  `addr_sub2` int(10) NOT NULL DEFAULT '0' COMMENT '二级地址id',
  `addr_sub3` int(10) NOT NULL DEFAULT '0' COMMENT '三级地址id',
  `count` int(11) NOT NULL DEFAULT '0' COMMENT '订单数，1 或 -1',
  `order_type_id` int(10) DEFAULT '-1' COMMENT '订单分类',
  `stock_area` tinyint(2) DEFAULT '-1' COMMENT '发货库区',
  `back_stock_area` tinyint(2) DEFAULT '-1' COMMENT '退货库区',
  PRIMARY KEY (`id`),
  KEY `index_1` (`order_id`),
  KEY `index_2` (`create_datetime`),
  KEY `index_3` (`data_type`),
  KEY `index_4` (`stock_area`)
) ENGINE=InnoDB AUTO_INCREMENT=1441 DEFAULT CHARSET=utf8 COMMENT='记录销售相关数据';

-- ----------------------------
-- Table structure for finance_sell_product
-- ----------------------------
DROP TABLE IF EXISTS `finance_sell_product`;
CREATE TABLE `finance_sell_product` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',
  `order_id` int(10) NOT NULL COMMENT '订单id',
  `adjust_code` varchar(16) DEFAULT NULL COMMENT '调整单号',
  `product_id` int(10) NOT NULL COMMENT '商品id',
  `buy_count` int(10) NOT NULL COMMENT '购买数量',
  `price` float(15,2) NOT NULL DEFAULT '0.00' COMMENT '商品定价',
  `dprice` float(15,2) NOT NULL DEFAULT '0.00' COMMENT '商品实际销售价',
  `price5` float(15,2) NOT NULL DEFAULT '0.00' COMMENT '成本价格',
  `product_line` int(10) NOT NULL COMMENT '新产品线id',
  `parent_id1` int(10) NOT NULL DEFAULT '0' COMMENT '一级分类',
  `parent_id2` int(10) NOT NULL DEFAULT '0' COMMENT '二级分类',
  `parent_id3` int(10) NOT NULL DEFAULT '0' COMMENT '三级分类',
  `create_datetime` datetime NOT NULL COMMENT '发生时间',
  `data_type` int(10) NOT NULL DEFAULT '0' COMMENT '数据类别',
  `balance_mode` tinyint(3) NOT NULL DEFAULT '0' COMMENT '是否有票，默认0有票',
  `supplier_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '供应商id',
  `finance_sell_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '发货信息表id',
