  KEY `index_2` (`create_datetime`),
  KEY `index_3` (`data_type`)
) ENGINE=InnoDB AUTO_INCREMENT=4129 DEFAULT CHARSET=utf8 COMMENT='记录销售明细相关数据';

-- ----------------------------
-- Table structure for finance_sell_report_plan
-- ----------------------------
DROP TABLE IF EXISTS `finance_sell_report_plan`;
CREATE TABLE `finance_sell_report_plan` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `start_date` datetime NOT NULL COMMENT '开始日期',
  `end_date` datetime NOT NULL COMMENT '结束日期',
  `stock_area` int(11) DEFAULT '-1' COMMENT '发货库区',
  `filename` varchar(50) DEFAULT '' COMMENT '生成的文件名称',
  `url` varchar(150) DEFAULT '' COMMENT '下载链接',
  `status` tinyint(4) NOT NULL COMMENT '状态：0 进行中，1 已完成，2 失败',
  `create_time` datetime NOT NULL COMMENT '订制时间',
  `type` tinyint(4) NOT NULL COMMENT '订制类型：0 整体收入，1 整体退货，2 买卖宝收入，3 买卖宝退货，4 广州迈世收入，5 广州迈世退货，6 无锡迈世收入，7 无锡迈世退货，8 北京迈世收入，9 北京迈世退货，10 成都迈世收入，11 成都迈世退货，12 京东青龙结算',
  `user_order_flat` varchar(100) DEFAULT NULL,
  `origin_filename` varchar(50) DEFAULT '' COMMENT '原始文件名',
  `origin_file_url` varchar(150) DEFAULT '' COMMENT '原始文件路径',
  `file_type` tinyint(4) DEFAULT '0' COMMENT '文件分类 0:默认值；1:成功数据；2：失败数据；',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=441 DEFAULT CHARSET=utf8 COMMENT='收入，退货明细报表订制表';

-- ----------------------------
-- Table structure for finance_sell_stat
-- ----------------------------
DROP TABLE IF EXISTS `finance_sell_stat`;
CREATE TABLE `finance_sell_stat` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT 'ID序号',
  `product_id` int(10) NOT NULL COMMENT '商品id',
  `product_code` varchar(16) NOT NULL COMMENT '商品编码',
  `product_name` varchar(45) NOT NULL COMMENT '商品名称',
  `product_parent_id1` int(10) NOT NULL COMMENT '一级分类id',
  `product_parent_name1` varchar(45) NOT NULL COMMENT '一级分类名称',
  `product_parent_id2` int(10) NOT NULL COMMENT '二级分类id',
  `product_parent_name2` varchar(45) NOT NULL COMMENT '二级分类名称',
  `proxy_id` int(10) NOT NULL COMMENT '供应商id',
  `proxy_name` varchar(45) NOT NULL COMMENT '供应商名称',
  `sell_count` int(10) NOT NULL COMMENT '销量',
  `cost_avg_price` float(15,2) NOT NULL COMMENT '商品成本价平均值',
  `cost_sum_price` float(15,2) NOT NULL COMMENT '成本金额',
  `sell_price` float(15,2) NOT NULL,
  `sell_real_avg_price` float(15,2) NOT NULL,
  `sell_sum_price` float(15,2) NOT NULL COMMENT '销售金额',
  `sell_real_sum_price` float(15,2) NOT NULL COMMENT '实际销售金额',
  `sell_cost_rate` float(15,2) NOT NULL COMMENT '实际销售成本率',
  `sell_profit` float(15,2) NOT NULL COMMENT '毛利',
  `sell_profit_rate` float(15,2) NOT NULL COMMENT '毛利率',
  `stat_start_datetime` datetime NOT NULL COMMENT '销售出库起始时间',
  `stat_end_datetime` datetime NOT NULL COMMENT '销售出库终止时间',
  PRIMARY KEY (`id`),
  KEY `index_1` (`product_id`),
  KEY `index_2` (`product_name`),
  KEY `index_3` (`product_code`),
  KEY `index_4` (`product_parent_id1`),
  KEY `index_5` (`product_parent_id2`),
  KEY `index_6` (`proxy_id`),
  KEY `index_7` (`stat_start_datetime`),
  KEY `index_8` (`stat_end_datetime`)
) ENGINE=InnoDB AUTO_INCREMENT=156 DEFAULT CHARSET=utf8 COMMENT='销售成本明细';

-- ----------------------------
-- Table structure for finance_settlement_party
