-- ----------------------------
-- Table structure for finance_stock_batch_history
-- ----------------------------
DROP TABLE IF EXISTS `finance_stock_batch_history`;
CREATE TABLE `finance_stock_batch_history` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `code` varchar(20) NOT NULL COMMENT '单据号',
  `product_id` int(10) NOT NULL COMMENT '产品id',
  `price` float(15,2) NOT NULL COMMENT '入库价格(不含税)',
