-- ----------------------------
-- Table structure for finance_stock_batch_history_copy
-- ----------------------------
DROP TABLE IF EXISTS `finance_stock_batch_history_copy`;
CREATE TABLE `finance_stock_batch_history_copy` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
