  KEY `idx_stock_type` (`stock_type`),
  KEY `idx_datetimes` (`log_date`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='财务批次历史数据拷贝';

-- ----------------------------
-- Table structure for finance_stock_card
