-- ----------------------------
-- Table structure for order_refund
-- ----------------------------
DROP TABLE IF EXISTS `order_refund`;
CREATE TABLE `order_refund` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `code` varchar(16) NOT NULL DEFAULT '' COMMENT '财务退款单编号',
  `order_id` int(10) NOT NULL DEFAULT '0' COMMENT '订单id',
  `after_sale_order_id` int(10) NOT NULL DEFAULT '0' COMMENT '售后单id 如果来自订单，则为0',
  `pay_type` tinyint(2) NOT NULL DEFAULT '0' COMMENT '在线支付类型 1:买卖宝钱包支付平台2:第三方支付平台3:银行卡4:(没有记录到支付渠道的或者目前后台提供的支付渠道接口中没列全),5充值卡支付,6混合支付',
  `pay_type_detail` varchar(25) DEFAULT '' COMMENT '在线支付类型明细，如买卖宝钱包，支付宝，财付通',
  `refund_account` varchar(25) DEFAULT '' COMMENT '退款账号',
  `refund_username` varchar(45) DEFAULT '' COMMENT '退款用户名',
  `refund_bank` varchar(45) DEFAULT NULL COMMENT '退款银行',
  `refund_reason_type` tinyint(2) NOT NULL DEFAULT '0' COMMENT '退款原因类型，和退款原因一一对应',
  `refund_reason_content` varchar(200) NOT NULL DEFAULT '' COMMENT '退款原因',
  `product_price` double DEFAULT '0' COMMENT '商品实际销售价格',
  `refund_price` double NOT NULL DEFAULT '0' COMMENT '退款金额',
  `operator` varchar(45) NOT NULL DEFAULT '' COMMENT '操作人',
  `refund_time` timestamp NULL DEFAULT NULL COMMENT '退款时间',
  `create_datetime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '退款单生成日期',
  `order_createtime` timestamp NULL DEFAULT NULL COMMENT '订单创建日期',
  `refund_fail_reason` varchar(100) DEFAULT '' COMMENT '退款失败原因',
  `refund_cancel_reason` varchar(100) DEFAULT '' COMMENT '退款取消原因',
  `status` tinyint(2) NOT NULL DEFAULT '0' COMMENT '订单退款状态 1,等待主管审核 2,等待财务退款 3,退款成功 4,退款失败 5,退款取消 6,等待财务审核',
  `refund_type` tinyint(1) NOT NULL COMMENT '退款类型 只有两种用于售前 1:正常单 2异常单,对于售后应该都是1：正常单',
  `refund_species` tinyint(1) NOT NULL DEFAULT '1' COMMENT '退款种类  1：售前退款  2：售后退款',
  PRIMARY KEY (`id`),
  KEY `Index_1` (`code`),
  KEY `Index_2` (`order_id`),
  KEY `Index_3` (`create_datetime`),
  KEY `Index_4` (`status`),
  KEY `Index_5` (`order_createtime`),
  KEY `Index_6` (`refund_time`),
  KEY `Index_7` (`after_sale_order_id`),
  KEY `Index_8` (`refund_species`)
) ENGINE=InnoDB AUTO_INCREMENT=324 DEFAULT CHARSET=utf8;

