  `product_type` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '产品类型',
  `product_count` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '产品数量',
  PRIMARY KEY (`id`),
  KEY `Index_2` (`code`),
  KEY `Index_3` (`order_id`),
  KEY `Index_5` (`create_datetime`),
  KEY `Index_6` (`last_oper_time`),
  KEY `Index_7` (`stock_area`,`stock_type`),
  KEY `Index_8` (`status`),
  KEY `Index_9` (`create_user_id`),
  KEY `Index_10` (`status_stock`),
  KEY `Index_11` (`real_status_stock`),
  KEY `Index_4` (`order_code`),
  KEY `index_test` (`create_datetime`,`stock_area`,`status`)
) ENGINE=InnoDB AUTO_INCREMENT=33595 DEFAULT CHARSET=utf8 COMMENT='出库单';

