  `draft_deadline` int(11) DEFAULT NULL COMMENT '汇票期限(单位：月)[纸质:1-6个月；电子:1-12个月]',
  `pay_use` tinyint(3) NOT NULL COMMENT '付款用途 1:预付款 2：货款 3:尾款 4：全额付款 5：费用单-样品 6：费用单-维修  7：费用单-耗材  8：费用单-运费  9：费用单-其他 10；费用单-新项目',
  `pay_for` tinyint(3) DEFAULT NULL COMMENT '付款对象[1:采购订单；2:供应商]',
  `lastest_pay_datetime` datetime DEFAULT NULL COMMENT '最迟打款时间',
  `create_admin_id` int(11) NOT NULL,
  `create_datetime` datetime DEFAULT NULL,
  `auditing_admin_id1` int(11) NOT NULL COMMENT '商品部部门经理审核管理员ID',
  `auditing_datetime1` datetime DEFAULT NULL,
  `auditing_admin_id2` int(11) NOT NULL COMMENT '财务部审核ID',
  `auditing_datetime2` datetime DEFAULT NULL,
  `pay_confirm_admin_id` int(11) NOT NULL COMMENT '最终到款确认管理员ID',
  `pay_confirm_datetime` datetime DEFAULT NULL,
  `status` tinyint(3) NOT NULL,
  `next_status` tinyint(3) DEFAULT NULL COMMENT '下一个审核通过的状态',
  `auditing_admin_id0` int(11) NOT NULL DEFAULT '0' COMMENT '商品部产品线经理审核管理员ID',
  `auditing_datetime0` datetime DEFAULT NULL,
  `supplier_bank_account_ids` varchar(100) DEFAULT NULL COMMENT '采购申请单对应银行账号',
  `has_invoice` tinyint(3) NOT NULL DEFAULT '0' COMMENT '有无增值税专用发票(0 —— 无；1 —— 专用；2 —— 普通)',
  `print_count` int(10) unsigned NOT NULL DEFAULT '0',
  `product_line` char(15) DEFAULT '服装',
  `auditing_admin_id3` int(11) NOT NULL DEFAULT '0' COMMENT '财务部门审核',
  `auditing_datetime3` datetime DEFAULT NULL,
  `auditing_admin_id4` int(11) NOT NULL COMMENT '财务不猛经理审核',
  `auditing_datetime4` datetime DEFAULT NULL,
  `remark` varchar(255) DEFAULT '' COMMENT '申请原因',
  `auditing_datetime5` datetime DEFAULT NULL COMMENT '财务总监审核时间',
  `auditing_admin_id5` int(11) DEFAULT NULL COMMENT '财务总监id',
  `buy_order_type` int(2) DEFAULT '2' COMMENT '采购订单类型[0:原采购；2:B2B采购]',
  PRIMARY KEY (`id`),
  KEY `index_1` (`supplier_id`,`type`),
  KEY `index_2` (`supplier_id`),
  KEY `index_3` (`type`),
  KEY `index_4` (`supplier_id`,`status`),
  KEY `index_5` (`status`)
