  KEY `channel` (`channel`) USING BTREE,
  KEY `type` (`type`) USING BTREE,
  KEY `createdate` (`createdate`) USING BTREE,
  KEY `status` (`status`) USING BTREE,
  KEY `code` (`code`(255)) USING BTREE
