  `weight` varchar(255) DEFAULT NULL COMMENT '重量',
  `image_path` varchar(255) DEFAULT NULL COMMENT '主图地址',
  `state` int(11) NOT NULL DEFAULT '0' COMMENT '上下架状态 1为上架，0为下架',
  `brand_name` varchar(255) DEFAULT NULL COMMENT '品牌',
  `name` varchar(255) DEFAULT NULL COMMENT '商品名称',
  `product_area` varchar(255) DEFAULT NULL COMMENT '产地',
  `upc` varchar(255) DEFAULT NULL COMMENT '条码',
  `sale_unit` varchar(255) DEFAULT NULL COMMENT '销售单位',
  `category` int(11) NOT NULL COMMENT '分类lid',
  `introduction_mobile` text COMMENT '手机版商品介绍',
  `introduction` text COMMENT 'pc版详细介绍',
  `param` text COMMENT '规格参数',
  `page_num` int(11) NOT NULL COMMENT '商品池子编号',
  `page_name` varchar(255) NOT NULL COMMENT '商品池子名称',
  `average_score` int(11) DEFAULT NULL COMMENT '商品评分 (5颗星，4颗星) 例如：5',
  `good_rate` varchar(255) DEFAULT NULL COMMENT '好评度',
  `general_rate` float DEFAULT NULL COMMENT '中评度',
  `poor_rate` float DEFAULT NULL COMMENT '差评度',
  `createtime` timestamp NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP COMMENT '入京东虚拟库的时间',
  `updatetime` timestamp NULL DEFAULT NULL COMMENT '信息更新时间',
  `jd_price` float DEFAULT NULL COMMENT '京东价格',
  `ware_qd` text COMMENT '包裹清单',
  `has_mobile_version` int(11) DEFAULT NULL COMMENT '是否有手机版 0：没有 1：有',
  `is_delete` int(1) NOT NULL DEFAULT '0' COMMENT '是否删除 0：未删除 1：删除',
  `is_complete` int(1) NOT NULL DEFAULT '0' COMMENT '商品信息是否录入完成 0：未完成 1：完成',
  `color` varchar(255) DEFAULT NULL COMMENT '销售属性 颜色',
  `size` varchar(255) DEFAULT NULL COMMENT '销售属性 尺寸',
  `custom` varchar(255) DEFAULT NULL COMMENT '销售属性 自定义',
  `stock` int(11) DEFAULT '0' COMMENT '库存',
  `market_price` float(11,0) DEFAULT NULL COMMENT '市场价',
  `adword` varchar(1024) DEFAULT NULL COMMENT '广告词',
  `description` varchar(1024) DEFAULT NULL COMMENT '描述',
  `quality` varchar(255) DEFAULT NULL COMMENT '保质期',
  `stock_state` int(2) DEFAULT '34' COMMENT '库存状态描述\r\n33 有货 现货-下单立即发货\r\n39 有货 在途-正在内部配货，预计2~6天到达本仓库\r\n40 有货 可配货-下单后从有货仓库配货\r\n36 预订\r\n34 无货\r\n',
  PRIMARY KEY (`id`),
  KEY `sku_id` (`sku_id`) USING BTREE,
  KEY `spi_id` (`spi_id`) USING BTREE,
  KEY `state` (`state`) USING BTREE,
  KEY `is_delete` (`is_delete`) USING BTREE,
  KEY `is_complete` (`is_complete`) USING BTREE,
  KEY `stock` (`stock`) USING BTREE,
  KEY `createtime` (`createtime`) USING BTREE,
  KEY `has_mobile_version` (`has_mobile_version`) USING BTREE,
  KEY `name` (`name`) USING BTREE,
  KEY `stock_state` (`stock_state`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=648 DEFAULT CHARSET=utf8;

