-- ----------------------------
-- Table structure for user_order
-- ----------------------------
DROP TABLE IF EXISTS `user_order`;
CREATE TABLE `user_order` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',
  `user_id` int(10) unsigned NOT NULL COMMENT '用户ID',
  `name` varchar(45) NOT NULL COMMENT '用户姓名',
  `phone` varchar(45) NOT NULL DEFAULT '' COMMENT '联系电话',
  `address` varchar(255) NOT NULL DEFAULT '' COMMENT '地址',
  `postcode` varchar(45) NOT NULL DEFAULT '' COMMENT '邮编',
  `buy_mode` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '购买方式：0货到付款1钱包支付2银行汇款4:混合支付',
  `product_count` int(10) unsigned NOT NULL DEFAULT '1' COMMENT '订单数量[无用字段]',
  `create_datetime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `flag` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '[无用字段]',
  `price` double NOT NULL DEFAULT '0' COMMENT '订单原价',
  `hide` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '[无用字段]',
  `deliver_type` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '[无用字段]',
  `remit_type` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '汇款银行',
  `confirm_datetime` timestamp NOT NULL DEFAULT '1980-01-01 00:00:00' COMMENT '预计发货时间',
  `status` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '状态，见user_order_status',
  `code` varchar(16) NOT NULL DEFAULT '' COMMENT '订单编码',
  `remark` text NOT NULL COMMENT '客服备注',
  `admin` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '处理的人员',
  `discount` float NOT NULL DEFAULT '1' COMMENT '折扣',
  `cp` varchar(16) DEFAULT '' COMMENT '真实手机号[无用字段]',
  `stockout` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '是否已经出货 1为已出货 0为未出货',
  `phone2` varchar(45) DEFAULT '' COMMENT '第二个联系电话',
  `prepay_deliver` float NOT NULL DEFAULT '0' COMMENT '预付款[无用字段]',
  `operator` varchar(50) DEFAULT '' COMMENT '处理人',
  `fr` int(10) unsigned DEFAULT '0' COMMENT '提交订单时，该次登录的友链id',
  `agent` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否代理商 1为代理商订单 0为普通订单 约束is_order[无用字段]',
  `agent_discount` float NOT NULL DEFAULT '1' COMMENT '代理折扣[无用字段]',
  `agent_mark` varchar(50) DEFAULT '' COMMENT '代理说明[无用字段]',
  `agent_remark` varchar(100) DEFAULT '' COMMENT '代理留言、备注[无用字段]',
  `is_order_reimburse` int(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否已经计算退货款[无用字段]',
  `is_reimburse` int(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否已经计算返还款[无用字段]',
  `real_pay` float NOT NULL DEFAULT '0' COMMENT '实际已到款[无用字段]',
  `postage` float NOT NULL DEFAULT '0' COMMENT '邮费',
  `is_order` int(1) unsigned NOT NULL DEFAULT '0' COMMENT '是进货订单还是退货订单，0代表进货订单，1代表退货订单[无用字段]',
  `dprice` double NOT NULL DEFAULT '0' COMMENT '打折后的价格',
  `images` varchar(255) NOT NULL DEFAULT '' COMMENT '[无用字段]',
  `areano` tinyint(2) unsigned NOT NULL DEFAULT '0' COMMENT '订单分类：0 - 北京订单 9 - 无锡订单',
  `pre_pay_type` tinyint(2) unsigned NOT NULL DEFAULT '0' COMMENT '[无用字段]',
  `is_olduser` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否为老用户订单,老用户为1 新用户为0',
  `suffix` float NOT NULL DEFAULT '0' COMMENT '订单的随机尾数，生成订单的时候添加，不能修改。[无用字段]',
  `contact_time` int(2) unsigned NOT NULL DEFAULT '0' COMMENT '用户方便联系的时间：0为随时、默认 1为9点-18点 2为18点-24点 3为24点以后',
  `united_orders` varchar(255) NOT NULL DEFAULT '' COMMENT '如果该订单是合并订单，则保存原订单的code，用逗号间隔',
  `flat` int(1) unsigned NOT NULL DEFAULT '0' COMMENT '订单来源 关联user_order_flat',
  `gender` int(1) unsigned NOT NULL DEFAULT '0' COMMENT '0 未识别 1男 2女',
  `web_remark` varchar(100) NOT NULL DEFAULT '' COMMENT '订单备注',
  `email` varchar(255) NOT NULL DEFAULT '' COMMENT '邮箱',
  `has_add_point` int(1) unsigned NOT NULL DEFAULT '0' COMMENT '[无用字段]',
  `origin_order_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '退换货使用：原订单ID[无用字段]',
  `new_order_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '退换货使用：新订单ID[无用字段]',
  `seller_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '下该订单的 销售人员的 ID[无用字段]',
  `deal_detail` varchar(100) NOT NULL DEFAULT '' COMMENT '建议配送时间',
  `cpa_status` int(1) unsigned NOT NULL DEFAULT '0' COMMENT '提成状态：0表示还未能提成，1表示预计可以提成，2表示确实可以提成[无用字段]',
  `cpa_bonus` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '提成金额[无用字段]',
  `cpa_pay` int(1) unsigned NOT NULL DEFAULT '0' COMMENT '提成支付情况：0表示未支付提成，1表示已支付提成[无用字段]',
  `seller_check_status` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '是否联系客户确认发货：0 表示未与客户电话联系确认发货 1 表示已经与客户电话联系确认发货[无用字段]',
  `stockout_remark` varchar(45) NOT NULL DEFAULT '' COMMENT '6月2发货备注',
  `consigner` varchar(45) NOT NULL DEFAULT '' COMMENT '发货人',
  `last_deal_time` datetime NOT NULL DEFAULT '1980-01-01 00:00:00' COMMENT '最后处理时间',
  `order_type` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '订单分类 关联user_order_type',
  `product_type` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '[无用字段]',
  `deliver` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '快递 1-邮局 2-昱达 3-宅急送 4-圆通',
  `stockout_deal` tinyint(2) unsigned NOT NULL DEFAULT '3' COMMENT '发货状态',
  `balance_status` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '[无用字段]',
  `createDate` varchar(255) DEFAULT NULL COMMENT '[无用字段]',
  PRIMARY KEY (`id`),
  KEY `Index_2` (`cp`),
  KEY `Index_3` (`code`),
  KEY `Index_4` (`phone`),
  KEY `Index_5` (`user_id`),
  KEY `Index_10` (`stockout_deal`),
  KEY `createTimeIdx` (`create_datetime`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=20101306 DEFAULT CHARSET=utf8 COMMENT='InnoDB free: 164864 kB';

